package de.cyb3rko.jabcodelib.test

import android.content.Context
import android.util.Base64
import androidx.test.ext.junit.runners.AndroidJUnit4
import androidx.test.platform.app.InstrumentationRegistry
import de.cyb3rko.jabcodelib.JabCodeLib
import java.io.File
import java.io.FileOutputStream
import org.junit.Assert.*
import org.junit.Test
import org.junit.runner.RunWith

private const val imagePositive = ""
private const val imageNegative = ""

/**
 * Instrumented test, which will scan image files with and without JAB Code
 */
@RunWith(AndroidJUnit4::class)
internal class JabCodeDetectTest {
    @Test
    fun scanSampleImagePositive() {
        val appContext = InstrumentationRegistry.getInstrumentation().targetContext
        val filePath = createFile(appContext, "test.png", imagePositive.base64ToBytes())
        val result = JabCodeLib().detect(filePath)
        assertNotEquals(result, null)
        assertNotEquals(result!!.size, 0)
        assertEquals(result.decodeToString(), "Hello World")
    }

    @Test
    fun scanSampleImageNegative() {
        val appContext = InstrumentationRegistry.getInstrumentation().targetContext
        val filePath = createFile(appContext, "test2.png", imageNegative.base64ToBytes())
        assertEquals(JabCodeLib().detect(filePath), null)
    }

    @Test
    fun scanEmptyFilePath() {
        assertEquals(JabCodeLib().detect(""), null)
    }

    private fun createFile(context: Context, fileName: String, data: ByteArray): String {
        val file = File(context.cacheDir, fileName)
        FileOutputStream(file).use {
            it.write(data)
        }
        assertEquals(file.exists(), true)
        return file.absolutePath
    }

    private fun String.base64ToBytes(): ByteArray {
        return Base64.decode(this, Base64.DEFAULT)
    }
}
